import React from "react";

const Weather = ({ inputCity, inputDays, weatherData, setWeatherData }) => {
  const componentDidMount = async () => {
    const url =
      "http://api.weatherapi.com/v1/forecast.json?key=971d4690217947ebb98152934201312&q=" +
      inputCity +
      "&days=" +
      inputDays;
    const response = await fetch(url);
    const data = await response.json();
    setWeatherData([...weatherData, { data: data }]);
    console.log(data);
  };
  return (
    <div>
      {weatherData.map((weather) => (
        <div>
          <p>
            Minimum Temperature:
            {weather.data.forecast.forecastday.day.mintemp_c}
          </p>
        </div>
      ))}
    </div>
  );
};

export default Weather;
